magn :: (Integer, Integer) -> Integer
magn (xx,yy) -- xx number, yy is always 1 and it returns the order of the number * 10 ie magn (321,1) will return 1000
	| xx `div` yy == 0 = yy
	| True = magn (xx, yy * 10)


multiply:: (Integer, Integer,Integer,[Integer]) -> (Integer,[Integer])
multiply(x,y,z,f) -- x original number ,y is the number of digits we want to multiply , z is the product of the digits , f is the numbers that were multiplied ie multiply(3242,3,1,[]) will return (16,[2 4 2])
	| y == 0 = (z,f)
	| True = multiply(x `div` 10, y -1,(x `mod` 10)*z,[(x `mod` 10)] ++ f)
	
	
maxim:: (Integer,Integer,[Integer], Integer) -> (Integer,[Integer])
maxim(a,b,c,e) -- a is the original number, b maxim product , c the maxim numbers stored , e number of digits to scan
	| magn (a,1) < 10 ^ e  = (b,c)
	| b == -1 = maxim((a `div` 10),fst (multiply(a,e,1,[])),snd (multiply(a,e,1,[])),e)
	| b /= -1 && fst (multiply(a,e,1,[])) > b  = maxim((a `div` 10),fst (multiply(a,e,1,[])),snd (multiply(a,e,1,[])),e)	
	| b /= -1 && fst (multiply(a,e,1,[])) <= b  = maxim((a `div` 10),b,c,e)		
	
	

main = print ( maxim( , -1,[],13) )